cb=colorbar('southoutside');
title(cb,'Coulomb stress transferred (bars)');
caxis([-CST_limit CST_limit])
xlabel('UTM x')
ylabel('UTM y')
zlabel('Depth (km)')
axis equal
hold on
%title(csv_filename)

% drawing grid lines 
for i=xmin:grid_interval:xmax
    line([i i],[ymin ymax],'Color','black')
end
for i=ymin:10:ymax
    line([xmin xmax],[i i],'Color','black')
end

end


